// 1~100까지의 합을 구한다.
public class 반복문 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      System.out.println("=== for문 ===");
		int sum=0;
       for(int i=1;i<=100;i++) 
       {
    	    sum=sum+i;
    	    System.out.println("i="+i+" ,sum="+sum);
       }
       
	}

}
