//1~10 ==> 홀수만

public class for3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    for(int i=2;i<=10;i+=2) 
    {
    	System.out.print(i+" ");
    	
    }int i=2;
    System.out.println();
    while(i<=10) 
    {
    	
    	System.out.print(i+" ");
    	i+=2;
    }
    	
	}

}
