public class 반복문정리 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     String msg="Hello java";
     System.out.println(msg);
     for(int i=msg.length()-1;i>=0;i--) {
    	 System.out.print(msg.charAt(i));
     }
	}

}
